test("#SRPSession compatible with java nimbus JS, U padding", (t) => {
  t.plan(1);
  class TestClientRoutines extends SRPRoutines {
    public generatePrivateValue(): bigint {
      return (
        BigInt(
          "",
        ) % this.parameters.N
      );
    }
  }

  class TestServerRoutines extends SRPRoutines {
    public generatePrivateValue(): bigint {
      return (
        BigInt(
          "",
        ) % this.parameters.N
      );
    }
  }

  const clientRoutines = new TestClientRoutines(new SRPParameters());

  const serverRoutines = new TestServerRoutines(new SRPParameters());

  const testUsername = "user";
  const testPassword = "&f-/9?7jT3U4D \\";
  const salt = BigInt(
    "",
  );
  const verifier = createVerifier(
    clientRoutines,
    testUsername,
    salt,
    testPassword,
  );

  const client = new SRPClientSession(clientRoutines);
  client.step1(testUsername, testPassword);

  const server = new SRPServerSession(serverRoutines);
  const B = server.step1(testUsername, salt, verifier);

  const credentials = client.step2(salt, B);
  const M2 = server.step2(credentials.A, credentials.M1);

  client.step3(M2);
  t.equals(
    "",
    client.S.toString(),
    "Session key is correct",
  );
});
